(function(){const themeToggleButton=document.getElementById("theme-toggle");const preferDark=window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches;const savedTheme=localStorage.getItem("theme");function applyTheme(theme){if(theme==="dark"){document.documentElement.classList.add("dark-mode")}else{document.documentElement.classList.remove("dark-mode")}}if(savedTheme){applyTheme(savedTheme)}else if(preferDark){applyTheme("dark")}else{applyTheme("light")}if(themeToggleButton){themeToggleButton.addEventListener("click",()=>{const currentTheme=document.documentElement.classList.contains("dark-mode")?"dark":"light";const newTheme=currentTheme==="dark"?"light":"dark";applyTheme(newTheme);localStorage.setItem("theme",newTheme)})}})();